
    // -----------------------------------------------------------------------
    // :: Pasive events to improve performance and stop the warning in Chrome
    // -----------------------------------------------------------------------
    function make_passive_event(name) {
        var data_name = name + '__handle__';
        return {
            setup: function( _, ns, handle ) {
                if (ns.includes('noPreventDefault')) {
                    this.addEventListener(name, handle, { passive: false });
                } else {
                    this.addEventListener(name, handle, { passive: true });
                }
                $(this).data(data_name, handle);
            },
            teardown: function() {
                var handle = $(this).data(data_name);
                this.removeEventListener(name, handle);
            }
        };
    }
    // -----------------------------------------------------------------------
    var passive_events = [
        'touchstart', 'touchmove', 'touchend', 'touchend', 'mousewheel', 'wheel'
    ];
    passive_events.forEach(function(event) {
        $.event.special[event] = make_passive_event(event);
    });
